mapper.select:
SELECT * FROM
   (
   SELECT ROWNUM as rn, tbl.* FROM
      (
      SELECT * FROM freeboard

      ORDER BY bno DESC
      ) tbl
   )

WHERE rn > (#{pageNum} - 1) * #{countPerPage}
AND rn <= #{pageNum} * #{countPerPage}

SELECT * FROM
   (
   SELECT ROWNUM as rn, tbl.* FROM
      (
      SELECT * FROM freeboard
      WHERE writer = #{id}
      ORDER BY bno DESC
      ) tbl
   )

WHERE rn > (#{page.pageNum} - 1) * #{page.countPerPage}
AND rn <= #{page.pageNum} * #{page.countPerPage}

SELECT COUNT(*)
FROM freeboard
WHERE writer = #{id}

mapper.update:
	  UPDATE freeboard
      SET title=#{title}, content=#{content}, updatedate = SYSDATE, uploadpath=#{uploadPath},filerealname=#{fileRealName}, photosize=#{photoSize},fileLoca=#{fileLoca},fileExtension=#{fileExtension}
      WHERE bno=#{bno}
